import logo from './logo.svg';
import React, { useEffect } from "react";
import './App.css';
import {moveCircles, headerTitle, tableOfContents, aboutMe, projects} from './section_script/sectionScripts.js'

function App() {

  useEffect(() => {
    headerTitle();
    moveCircles();
    window.addEventListener('scroll', handleScroll); // Add a scroll event listener
    return () => {
      window.removeEventListener('scroll', handleScroll); // Remove the scroll event listener on unmount
    }
  }, []);

  const handleScroll = () => {
    if (document.querySelector(".App-header").getBoundingClientRect().top < 0) {
      tableOfContents();
    }

    const tocRect = document.querySelector(".App-table-of-contents").getBoundingClientRect();

    if (tocRect.top < -(tocRect.height / 2)) {
      aboutMe();
    }

    const abtmeRect = document.querySelector(".App-about-me").getBoundingClientRect();

    if (abtmeRect.top < -(abtmeRect.height / 1.2)) {
      projects();
    }
  }

  return (

    <div className="App">
      <header className="App-header">
        <div className="circle small"></div>
        <div className="circle small"></div>
        <div className="circle small"></div>
        <div className="circle small"></div>
        <div className="circle medium"></div>
        <div className="circle medium"></div>
        <div className="circle medium"></div>
        <div className="circle medium"></div>
        <div className="circle large"></div>
        <div className="circle large"></div>
        <div className="circle large"></div>
        <div className="circle large"></div>
        <canvas id="header-canvas"></canvas>
      </header>
      <div className="App-table-of-contents">
        <canvas id="table-of-contents-canvas"></canvas>
      </div>
      <a id="AboutMe"></a>
      <div className="App-about-me">
        <canvas id="about-me-canvas"></canvas>
      </div>
      <a id="Projects"></a>
      <div className="App-projects">
        <canvas id="projects-canvas"></canvas>
        <div className="App-projects-select-container" id="App-projects-select-container">
          <div className="App-projects-select-container-nav">
            <select name="sortby" id="sort"></select>
          </div>
        </div>
      </div>

      <footer class="footer">Jasper Charlinski, Feb 16th 2023 &copy;</footer>
    </div>
  );
}

export default App;
