      const yDirection = circle.getAttribute('data-y-direction');
      let x = parseInt(circle.style.left) || 0;
      let y = parseInt(circle.style.top) || 0;

      // Calculate the new position
      x += xDirection * speed;
      y += yDirection * speed;

      // Check if the circle hit the left or right border
      if (x <= 0 || x + circle.clientWidth >= window.innerWidth) {
        circle.setAttribute('data-x-direction', -xDirection);
        xDirection *= -1;
      }

      // Check if the circle hit the top or bottom border
      if (y <= 0 || y + circle.clientHeight >= AppHeader.clientHeight) {
        circle.setAttribute('data-y-direction', -yDirection);
        yDirection *= -1;
      }

      // Update the position
      circle.style.left = x + 'px';
      circle.style.top = y + 'px';
