export function headerAnimation() {

    const ctx = document.getElementById("header-canvas").getContext("2d");
    const titleText = "Welcome To Jasper's Portfolio";
    const header = document.createElement("h1"); 
    header.textContent = titleText; 
    header.classList.add("animate-in", "title"); 

    const subtitleText = "Enjoy Your Stay!";
    const subtitle = document.createElement("h3"); 
    subtitle.textContent = subtitleText; 
    subtitle.classList.add("animate-in", "subtitle"); 

    if (!ctx.canvas.parentNode.querySelector("h1")) {
        ctx.canvas.parentNode.appendChild(header, ctx.canvas);
    }

    setTimeout(() => {

        if (!ctx.canvas.parentNode.querySelector("h3")) {
            ctx.canvas.parentNode.appendChild(subtitle, ctx.canvas);
        }
    }, 500)

    ctx.canvas.style.display = "none";
}


export function moveCircles() {
    const circles = document.querySelectorAll(".circle");

    circles.forEach((circle) => {
      // Get current circle position
      const currentTransform = window.getComputedStyle(circle).getPropertyValue("transform");
      const currentX = parseFloat(currentTransform.split(",")[4]);
      const currentY = parseFloat(currentTransform.split(",")[5]);

      // Calculate random movement direction and distance
      const angle = Math.random() * 2 * Math.PI;
      const distance = Math.random() * 10;

      // Calculate new circle position
      const x = currentX + distance * Math.cos(angle);
      const y = currentY + distance * Math.sin(angle);

      // Move circle closer to new position using requestAnimationFrame
      function animate() {
        // Get current circle position
        const currentTransform = window.getComputedStyle(circle).getPropertyValue("transform");
        const currentX = parseFloat(currentTransform.split(",")[4]);
        const currentY = parseFloat(currentTransform.split(",")[5]);

        // Calculate distance to new position
        const distanceToNewPosition = Math.sqrt(
          Math.pow(x - currentX, 2) + Math.pow(y - currentY, 2)
        );

        // Move circle closer to new position
        if (distanceToNewPosition > 1) {
          const deltaX = (x - currentX) / distanceToNewPosition;
          const deltaY = (y - currentY) / distanceToNewPosition;

          circle.style.transform = `translate(${currentX + deltaX}px, ${currentY + deltaY}px)`;

          requestAnimationFrame(animate);
        }
      }

      requestAnimationFrame(animate);
    });
  }



export function audioVisualizer()
{
    const canvas = document.getElementById(`header-canvas`);
    const ctx = canvas.getContext(`2d`);
    console.log(canvas);
    console.log("hellow");

    const audioCtx = new AudioContext();
    const analyser = audioCtx.createAnalyser();
    const audio = new Audio(`../ext_song.mp3`);
    const audioSource = audioCtx.createMediaElementSource(audio);
    audioSource.connect(analyser);

    analyser.fftSize = 1024;

    const bufferLen = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLen);

    function animateFullRainbowBar() {

        if (audio.paused)
        {
            audio.play();
        }

        const barWidth = canvas.width / bufferLen;
        let barHeight;
        let x = 0;
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        analyser.getByteFrequencyData(dataArray);

        for (let i = 0; i < bufferLen; i++)
        {
            barHeight = dataArray[i] * 2;

            const hue = i * 10;

            ctx.fillStyle = `hsl(` + hue + `,100%, 50%)`;

            // let red = 100;
            // let blue = barHeight + 30;
            // let green = barHeight + 50;

            ctx.fillRect(x, canvas.height - barHeight, barWidth, barHeight);

            ctx.save();
            ctx.translate(canvas.width, 0);
            ctx.scale(-1, 1);   
            ctx.fillRect(x, canvas.height - barHeight, barWidth, barHeight);
            ctx.restore();

            x += barWidth + 5;

            if (x > canvas.width/2)
            {
                x = 0;
                break;
            }
        }

        requestAnimationFrame(animateFullRainbowBar);
    }

    audio.play();
    animateFullRainbowBar();
}
