import logo from './logo.svg';
import React, { useEffect } from "react";
import './App.css';
import {moveCircles, headerTitle, tableOfContents, aboutMe, projects} from './section_script/sectionScripts.js'

function App() {

  useEffect(() => {
    headerTitle();
    moveCircles();
    window.addEventListener('scroll', handleScroll); // Add a scroll event listener
    return () => {
      window.removeEventListener('scroll', handleScroll); // Remove the scroll event listener on unmount
    }
  }, []);

  const handleScroll = () => {
    if (document.querySelector(".App-header").getBoundingClientRect().top < 0) {
      tableOfContents();
    }

    const tocRect = document.querySelector(".App-table-of-contents").getBoundingClientRect();

    if (tocRect.top < -(tocRect.height / 2)) {
      aboutMe();
    }

    const abtmeRect = document.querySelector(".App-about-me").getBoundingClientRect();

    if (abtmeRect.top < -(abtmeRect.height / 1.2)) {
      projects();
    }
  }

  const sortBy = (event) => {

    const sortByOption = document.getElementById("sortby").value;
    console.log(sortByOption);

    if (sortByOption == "language")
    {
      const languageOptions = document.createElement("div");
      languageOptions.innerHTML = `
      <label>
      <input type="checkbox" name="cplus" value="C++">
      C++
      </label>
      <label>
        <input type="checkbox" name="python" value="Python">
        Python
      </label>
      <label>
        <input type="checkbox" name="javascript" value="JavaScript">
        JavaScript
      </label>
      <label>
        <input type="checkbox" name="htmlcss" value="HTML/CSS">
        HTML/CSS
      </label>
      <label>
        <input type="checkbox" name="bash" value="Bash">
        Bash
      </label>
      <label>
        <input type="checkbox" name="racket" value="Racket">
        Racket
      </label>
      <label>
        <input type="checkbox" name="wordpress" value="WordPress">
        WordPress
      </label>
      `;

      // document.getElementById("projects-nav").appendChild(languageOptions);
    }

  };

  return (

    <div className="App">
      <header className="App-header">
        <div className="circle small"></div>
        <div className="circle small"></div>
        <div className="circle small"></div>
        <div className="circle small"></div>
        <div className="circle medium"></div>
        <div className="circle medium"></div>
        <div className="circle medium"></div>
        <div className="circle medium"></div>
        <div className="circle large"></div>
        <div className="circle large"></div>
        <div className="circle large"></div>
        <div className="circle large"></div>
        <canvas id="header-canvas"></canvas>
      </header>
      <div className="App-table-of-contents">
        <canvas id="table-of-contents-canvas"></canvas>
      </div>
      <a id="AboutMe"></a>
      <div className="App-about-me">
        <canvas id="about-me-canvas"></canvas>
      </div>
      <a id="Projects"></a>
      <div className="App-projects">
        <canvas id="projects-canvas"></canvas>
        <div className="App-projects-select-container" id="App-projects-select-container">
          {/* <div className="App-projects-select-container-nav" id="projects-nav">
            <label></label>Sort By: 
            <select name="sortby" id="sortby" className='App-projects-select-container-nav-select' onChange={sortBy}>
              <option value="best">Best</option>
              <option value="language">Language</option>
              <option value="new">Date - New</option>
              <option value="old">Date - Old</option>
            </select>
          </div> */}
        </div>
      </div>
      <footer class="footer">Jasper Charlinski, Feb 16th 2023 &copy;</footer>
    </div>
  );
}

export default App;
