import React, { useEffect, useState } from "react";
import './App.css';
import { moveCircles, headerTitle, tableOfContents, aboutMe, projects, sortBy, checkSort } from './section_script/sectionScripts.js';

function App() {

  useEffect(() => {
    headerTitle();
    moveCircles();
    window.addEventListener('scroll', handleScroll);

    if (document.readyState === "complete") {
      loaded();
    } else {
      window.addEventListener('load', loaded);
      return () => document.removeEventListener('load', loaded);
    }     
    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('load', loaded);
    }
  }, []);

  function loaded() {

    console.log("loaded")
    const audioBackground = document.getElementById("audioBackground");

    console.log(audioBackground);
  }


  const handleScroll = () => {
    if (document.querySelector(".App-header").getBoundingClientRect().top < 0) {
      tableOfContents();
    }

    const tocRect = document.querySelector(".App-table-of-contents").getBoundingClientRect();

    if (tocRect.top < -(tocRect.height / 2)) {
      aboutMe();
    }

    const abtmeRect = document.querySelector(".App-about-me").getBoundingClientRect();

    if (abtmeRect.top < -(abtmeRect.height / 1.2)) {
      projects();
    }
  }

  const [checkedBoxes, setCheckedBoxes] = useState({
    "C++": true,
    "Python": true,
    "JavaScript": true,
    "HTML/CSS": true,
    "Bash": true,
    "Racket": true,
    "WordPress": true,
    "PHP": true,
    "SQL": true
  });

  const handleCheckboxChange = (event) => {
    const checkboxName = event.target.name;
    const isChecked = event.target.checked;
    setCheckedBoxes({
      ...checkedBoxes,
      [checkboxName]: isChecked
    });

    checkSort(checkboxName);
  }

  return (

    <div className="App">
      <header className="App-header">
        <div className="circle small"></div>
        <div className="circle small"></div>
        <div className="circle small"></div>
        <div className="circle small"></div>
        <div className="circle medium"></div>
        <div className="circle medium"></div>
        <div className="circle medium"></div>
        <div className="circle medium"></div>
        <div className="circle large"></div>
        <div className="circle large"></div>
        <div className="circle large"></div>
        <div className="circle large"></div>
        <canvas id="header-canvas"></canvas>
      </header>
      <div className="App-table-of-contents">
        <canvas id="table-of-contents-canvas"></canvas>
      </div>
      <a id="AboutMe"></a>
      <div className="App-about-me">
